/**
 * Create a new object containing only the specified keys
 */
export declare const pick: <T extends object, K extends keyof T>(obj: T, ...keys: readonly K[]) => Pick<T, K>;
/**
 * Create a new object excluding the specified keys
 */
export declare function omit<T extends object, K extends keyof T>(obj: T, ...keys: readonly K[]): Omit<T, K>;
